window.onload=function(){var e=document.getElementById("accordion"),t=e.getElementsByTagName("h2"),n=t.length,r,i;for(r=0;r<n;r++){i=t[r];i.setAttribute("class","toggle");i.nextElementSibling.setAttribute("class","closed");i.onclick=function(){var e=this;if(e.getAttribute("class")=="toggle"){e.setAttribute("class","toggle-active");e.nextElementSibling.setAttribute("class","")}else{e.setAttribute("class","toggle");e.nextElementSibling.setAttribute("class","closed")}}}}